import { FC, ReactNode } from "react";
import { Logo } from "../../../design-system/Logo";
import styled from "styled-components";

const AuthWrapperBase = styled.main`
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: 100vh;
`;

const AuthForm = styled.section<{ $switchLayout?: boolean }>`
    grid-row: 1 / 2;
    grid-column: ${(props) => (props.$switchLayout ? "2 / 3" : "1 / 2")};

    padding: var(--space-50);
    display: flex;
    align-items: center;
    justify-content: center;
`;

const AuthContent = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: var(--space-50);
    flex-basis: 57rem;
`;

const AuthImageWrapper = styled.section<{ $switchLayout?: boolean }>`
    grid-row: 1 / 2;
    grid-column: ${(props) => (props.$switchLayout ? "1 / 2" : "2 / 3")};
    padding: var(--space-50);
`;

const AuthImage = styled.img.attrs({
    alt: "Office"
})`
    height: 100%;
    width: 100%;
    border-radius: var(--space-50);
    object-fit: cover;
    transition: all 1s;

    &:hover {
        transform: scale(1.1);
    }
`;

type AuthWrapperProps = {
    imageUrl: string;
    children: ReactNode;
    pageTitle: string;
};

const AuthWrapper: FC<AuthWrapperProps> = ({
    imageUrl,
    pageTitle,
    children
}) => {
    return (
        <Wrapper className="auth-wrapper">
            <AuthForm>
                <AuthContent>
                    <Logo layout="vertical" size="lg" customText={pageTitle} />
                    {children}
                </AuthContent>
            </AuthForm>
            <AuthImg>
                <img src={imageUrl} alt="Projectify App | Hands making star" />
            </AuthImg>
        </Wrapper>
    );
};

export { AuthWrapper };
